mapper.insert:
INSERT INTO operation_logs (
    username, user_id, ip, method, request_url,
    request_type, request_params, response_result,
    operation, is_success, error_message, operation_time
) VALUES (
             #{username}, #{userId}, #{ip}, #{method}, #{requestUrl},
             #{requestType}, #{requestParams}, #{responseResult},
             #{operation}, #{isSuccess}, #{errorMessage}, #{operationTime}
         )
